import React, {Component} from 'react';
// import chaptersData from '../chaptersData';
// import {API_BASE_URL} from '../clientServerRoom/config';

class TheList extends Component {
  constructor(props){
    super(props);
    this.state = {
      chapters: []
    };
  }
  componentDidMount() {
    fetch('/api/ChaptersForStory/all')
    .then(response => response.json())
    .then(data => {
      const chapters = Object.keys(data)
            .map(key => data[key].item[0]);
            this.setState({
              chapters
            });
    });
  }
  render(){
    return(
        <ul className='coverList'>
          <li>
              <div>Chapter: {this.state.chapters.chapterNum}</div>
              <div>Title: {this.state.chapters.title}</div>
            </li>
        </ul>
    );
  }
}


