import React, {Component} from 'react';

class ModalBox extends Component {
  state = {
    show: false
  };
  showModal = e => {
    this.setState({
      show: true
    });
  };
  render(){
    if(!this.props.show){
      return null;
    }
    return (
      <button className='modal' onClick={e => {
        this.showModal();
      }}>Open the whatever it is...</button>
    )
  }
}
export default ModalBox;
