import React, {Component} from 'react';
import {Tab, Tabs, TabList, TabPanel} from 'react-tabs';
import {NavLink} from 'react-router-dom';

class TheEnd extends Component{
  constructor(props){
    super(props);
    this.state = {
      summaryChapters: []
    };
  }
  componentDidMount() {
    fetch('/api/ChaptersForStory/all')
    .then(response => response.json())
    .then(data => this.setState({chapterlists: data.summaryChapters}));
  }
  render(){
    const {summaryChapters} = this.state;
    return(
      <div>
        <Tabs>
          <TabList>
            <Tab>Your Progress</Tab>
            <Tab>Compare Stats</Tab>
            <Tab>The End Tab</Tab>
          </TabList>
          <TabPanel>
            <p>
              Test1 Progress
            </p>
            <div className='progressContainer'>
            {summaryChapters.map(summaryChapter =>
              <div className='progressBox' key={summaryChapter._id}>
                <p>Chapter: {summaryChapter.chapterNum}</p>
                <p>Title: {summaryChapter.title}</p>
              </div>
            )}
              <div className='progressBox'></div>
              <div className='progressBox'></div>
              <div className='progressBox'></div>
              <div className='progressBox'></div>
              <div className='progressBox'></div>
              <div className='progressBox'></div>
            </div>
          </TabPanel>
          <TabPanel>
            <p>
              Test2 Stats
            </p>
              <div className='statsContainer'>
                <div className='statsBars'></div>
                <div className='statsBars'></div>
                <div className='statsBars'></div>
                <div className='statsBars'></div>
                <div className='statsBars'></div>
              </div>
          </TabPanel>
          <TabPanel>
            <NavLink to='/' activeClassName='exitLink'><button className='btn draw-border'>Exit back to the beginning</button></NavLink>
          </TabPanel>
        </Tabs>
      </div>
    );
  }
}


export default TheEnd;
