      show: true
    });
  };
  render(){
    return (
      <div>
      <button className='modal' onClick={e => {
        this.showModal();
      }}>Open the whatever it is...</button>
      <ModalBox show={this.state.show} />
        <p>{this.state.responseToPost}</p>
      </div>
    )
  }
}







