import React, {Component} from 'react';
import {Tab, Tabs, TabList, TabPanel} from 'react-tabs';
import {NavLink} from 'react-router-dom';
import EditModal from './editModal';
import DeleteSvg from '../SvgIcons/times-circle-solid.svg';
import EditSvg from '../SvgIcons/keyboard-solid.svg';

class TheEnd extends Component{
  constructor(){
    super();
    this.state = {
      selectedChapterNum: null,
      selectedTitle: null,
      selectedContent: null
    }
  }
  handleClick(chapterNum, title, content){
    let selectedChapterNum = this.state.selectedChapterNum === chapterNum ? null : chapterNum;
    let selectedTitle = this.state.selectedTitle === title ? null : title;
    let selectedContent = this.state.selectedContent === content ? null : content;
    this.setState({selectedChapterNum, selectedTitle, selectedContent});
  }
  state = {
    show: false
  };
  openEditModal = e => {
    this.setState({
      show: !this.state.show
    });
  };

  async deleteChapter(chapterlist){
    if(window.confirm(`Are you sure about delete this "${chapterlist.title}" chapter?`)) {
      fetch('/api/FinalizeTheStory/' + `${chapterlist.id}`, {
        method: 'DELETE',
      })
      .then(res => res.text())
      .then(res => console.log(res));
    //   .then(function(response) {
    //     if (!response.ok) {
    //         throw Error(response.statusText);
    //     }
    //     return response;
    // }).then(function(response) {
    //     console.log("ok");
    // }).catch(function(error) {
    //     console.log(error);
    // });
      // await this.fetch('delete', `/api/FinalizeTheStory/${chapterlist.id}`);
      // this.callApi();
    }
  }


  render(){
    return(
      <div>
        <Tabs>
          <TabList>
            <Tab>Your Story</Tab>
            <Tab>Comparison Stats</Tab>
            <Tab>Exit</Tab>
          </TabList>
          <TabPanel>
            <h2>
              The Story
            </h2>
            <div className='progressContainer'>
            <ul className='progressLine'>
              {this.props.chapterlists.map(chapterlist => <li className='progressBox' key={chapterlist.id}>
                  <button className='deleteChapter' onClick={() => this.deleteChapter(chapterlist)}><img src={DeleteSvg} /></button>
                  <div>Chapter: {chapterlist.chapterNum}</div>
                  <div>Title: {chapterlist.title}</div>
                  <button className='editTheChapter' onClick={e => { this.openEditModal(e); }}><img src={EditSvg} onClick={this.handleClick.bind(this, chapterNum, title, content)} value={chapterlist.chapterNum, chapterlist.title, chapterlist.content} /></button>
                </li>)}
            </ul>
            </div>
            <EditModal onClose={this.showModal} show={this.state.show}>
            <form className='editStory' onSubmit={this.handleSubmit}>
              <input className='editChapterNumber' type='text' placeholder='Chapter-nth?' value={`${this.state.selectedChapterNum}`} onChange={e => this.setState({chapterNum: e.target.value})} name='chapter' />
              <br />
              <input className='editTitleInput' type='text' placeholder='Type your title' value={`${this.state.selectedTitle}`} onChange={e => this.setState({title: e.target.value})} name='title' />
              <br />
              <textarea className='editStoryBox' value={`${this.state.selectedContent}`} onChange={e => this.setState({content: e.target.value})} name='content'>Go crazy!</textarea>
              <button className='editChapterSubmit' type='submit'>Edit it</button>
              <button className='edit-modal close-btn' onClick={e => { this.openEditModal(e); }}><img  /></button>
            </form>
            </EditModal>
          </TabPanel>
          <TabPanel>
            <p>
               Overall Stats with other users
            </p>
              <div className='statsContainer'>
                <div className='statsBars'></div>
                <div className='statsBars'></div>
                <div className='statsBars'></div>
                <div className='statsBars'></div>
                <div className='statsBars'></div>
              </div>
          </TabPanel>
          <TabPanel>
            <NavLink to='/' activeClassName='exitLink'><button className='btn draw-border'>Exit back to Front Page</button></NavLink>
          </TabPanel>
        </Tabs>
      </div>
    );
  }
}


export default TheEnd;
